#store seqs to compare as strings
seq1 = ''
seq2 = ''

#compare each nt of seq 1 and seq2 in same position
ham = ""
n = 0
for char in seq1[0::]:
    if seq2[n] != char:
        ham += char
    n += 1

print(len(ham))
#More efficient code
print(len([char for index, char in enumerate(seq1) if seq2[index] != char]))